(174, 1, '2017-10-11 16:01:59', '2017-10-11 16:01:59', '{\n    "lens::lens_slide_title1": {\n        "value": "Graduation Day",\n        "type": "theme_mod",\n        "user_id": 1\n    },\n    "lens::lens_slide_desc1": {\n        "value": "students of class 2017 in TNTU ",\n        "type": "theme_mod",\n        "user_id": 1\n    },\n    "lens::lens_slide_url1": {\n        "value": "",\n        "type": "theme_mod",\n        "user_id": 1\n    },\n    "lens::lens_slide_title2": {\n        "value": "Tourist Trip",\n        "type": "theme_mod",\n        "user_id": 1\n    },\n    "lens::lens_slide_desc2": {\n        "value": "Skating on the Carpathian mountains , Ukraine ",\n        "type": "theme_mod",\n        "user_id": 1\n    },\n    "lens::lens_slide_title3": {\n        "value": "Memories in Ternopil",\n        "type": "theme_mod",\n        "user_id": 1\n    },\n    "lens::lens_slide_desc3": {\n        "value": "some pictures in Ternopil city and around it",\n        "type": "theme_mod",\n        "user_id": 1\n    },\n    "lens::lens_slide_title4": {\n        "value": "Vinnytsia",\n        "type": "theme_mod",\n        "user_id": 1\n    },\n    "lens::lens_slide_desc4": {\n        "value": "A trip to one of the most beautiful cities in Ukraine",\n        "type": "theme_mod",\n        "user_id": 1\n    },\n    "lens::lens_main_showcase_enable": {\n        "value": false,\n        "type": "theme_mod",\n        "user_id": 1\n    },\n    "lens::lens_showcase_img1": {\n        "value": "",\n        "type": "theme_mod",\n        "user_id": 1\n    },\n    "lens::lens_showcase_title1": {\n        "value": "",\n        "type": "theme_mod",\n        "user_id": 1\n    },\n    "lens::lens_main_aboutme_enable": {\n        "value": true,\n        "type": "theme_mod",\n        "user_id": 1\n    },\n    "lens::lens_aboutme_title": {\n        "value": "About Me",\n        "type": "theme_mod",\n        "user_id": 1\n    },\n    "lens::lens_aboutme_name": {\n        "value": "Shiyar Ali",\n        "type": "theme_mod",\n        "user_id": 1\n    },\n    "lens::lens_aboutme_desc": {\n        "value": "My name is Shiyar Ali  from Syria, I left Syria after the beginning of the war there in 2012, I got a bachelor\\u2019s degree from the National Technical University of Ukraine Ternopil city in 2017\\nProgrammer, I know many programming languages, I have many projects in the Arduino board, speak five languages, participated in several scientific presentations to make my projects, my wish to become a professional programmer in the world, and making robots.",\n        "type": "theme_mod",\n        "user_id": 1\n    },\n    "lens::lens_social_url2": {\n        "value": "https://twitter.com/shiyaroo",\n        "type": "theme_mod",\n        "user_id": 1\n    },\n    "lens::lens_social_url3": {\n        "value": "https://plus.google.com/u/1/108997998931021318421",\n        "type": "theme_mod",\n        "user_id": 1\n    },\n    "lens::lens_social_url5": {\n        "value": "https://www.youtube.com/user/rrodi1980",\n        "type": "theme_mod",\n        "user_id": 1\n    },\n    "lens::lens_social_url4": {\n        "value": "https://www.instagram.com/shiyar.kurdish/",\n        "type": "theme_mod",\n        "user_id": 1\n    }\n}', '', '', 'trash', 'closed', 'closed', '', '86685bd4-de67-47e8-a493-05225c5a45ae', '', '', '2017-10-11 16:01:59', '2017-10-11 16:01:59', '', 0, 'http://localhost:8080/wp/?p=174', 0, 'customize_changeset', '', 0),
